import {useEffect} from "react"
import { useSelector ,useDispatch} from 'react-redux';
import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import {uiActions} from "./store/ui-slice";
import Notification from "./components/UI/Notification"
function App() {
  const showCart=useSelector(state=>state.ui.cartIsVisible);
  const cart =useSelector((state)=>state.cart);
  const dispatch=useDispatch();
  const notfication=useSelector(state=>state.ui.notfication)
  useEffect(() => {
    const sendCartData=async()=>{
      dispatch(uiActions.showNotification({
        status:"pending",
        title:"Sending...",
        message:"Sending cart data!"
      }));
      const response=await  fetch("https://food-app-b8b01-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json",
      {method:"PUT",body:JSON.stringify(cart),});
      if(!response.ok){
        throw new Error("Sending cart data failed")
      }
      dispatch(uiActions.showNotification({
        status:"success",
        title:"Success!",
        message:"Send cart data successfully!"
      }));
    };
    sendCartData().catch(erroe=>{
      dispatch(uiActions.showNotification({
        status:"error",
        title:"Error!",
        message:"Sending cart data failed!"
      }));
    });
  }, [cart,dispatch])

  return (
    <>
    {notfication && (<Notification status={notfication.status} title={notfication.title} message={notfication.message}></Notification>)}
    <Layout>
      {showCart && <Cart />}
      <Products />
    </Layout>
    </>
  );
}

export default App;
